document.addEventListener("DOMContentLoaded", function () {
    window.addEventListener('scroll', function () {
        if (window.scrollY > 70) {
            document.getElementById('header__navbar').classList.add('fixed-top');
            document.getElementById('brand').classList.remove('hidden');
            document.getElementById('logo').classList.add('hidden');
            // add padding top to show content behind navbar
            navbar_height = document.querySelector('.navbar').offsetHeight;
            document.body.style.paddingTop = navbar_height + 'px';
        } else {
            document.getElementById('header__navbar').classList.remove('fixed-top');
            document.getElementById('brand').classList.add('hidden');
            document.getElementById('logo').classList.remove('hidden');
            // remove padding top from body
            document.body.style.paddingTop = '0';
        }
    });
}); 